const initialState = {
        books: [],
        loading: true,
        error: null,
        cartItems: [],
        orderTotal: 0
};

const updateCartItems = (cartItems, item, idx) => {

    if (item.count === 0) {
        return [
            ...cartItems.slice(0, idx),
            ...cartItems.slice(idx + 1)
        ];
    }

    if (item.count === -1) {
        return [
            ...cartItems,
            item
        ];
    };

    return [
        ...cartItems.slice(0, idx),
        item,
        ...cartItems.slice(idx + 1)
    ];
};

const updateOrderTotal = (state, book, quantity) => {
    const {orderTotal, cartItems} = state;
    const {id} = book;
    const itemIndex = cartItems.findIndex(({id}) => id === id);


    console.log(state);
    console.log(book);
    console.log(itemIndex);
    console.log(cartItems);

    if (quantity === 1) {
        const result = orderTotal + book.price;
        return result;
    }

    if (quantity === -1) {
        const result = orderTotal - book.price;
        return result;
    }

    if (quantity < -1) {
        // cartItems.id
        const result = 0;
        return result;
    }        
    
}

const updateCartItem = (book, item = {}, quantity) => {
    const { id = book.id, count = 0, title = book.title, total = 0 } = item;

  return {
      id,
      title,
      count: count + quantity,
      total: total + quantity*book.price
  };
};

const updateOrder = (state, bookId, quantity) => {
    const {  books,  cartItems } = state;
    const book = books.find((book) => book.id === bookId); // в переменную book записываю объект выбранной книжки
    const itemIndex = cartItems.findIndex(({id}) => id === bookId);
    const item = cartItems[itemIndex];
    const newItem = updateCartItem(book, item, quantity);  // тут есть total в котором общая цена
    

        return {
           
            ... state,
            cartItems: updateCartItems(state.cartItems, newItem, itemIndex),
            orderTotal: updateOrderTotal(state, book, quantity)
        };

};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'BOOK_ADDED_TO_CART':
            return updateOrder(state, action.payload, 1);

        case 'BOOK_REMOVED_FROM_CART':
                return updateOrder(state, action.payload, -1);

        case 'ALL_BOOKS_REMOVED_FROM_CART':
            const item = state.cartItems.find(({id}) => id === action.payload);
            return updateOrder(state, action.payload, -item.count);
            default:
                    return state;   

        case 'FETCH_BOOKS_REQUESTED':
                            return {
                                ... state,
                                books: [],
                                loading: true,
                                error: null
                
                            };
        case 'FETCH_BOOKS_SUCCESS':
                            return {
                                ... state,
                                books: action.payload,
                                loading: false,
                                error: null
                            };
        case 'FETCH_BOOKS_FAILURE':
                            return {
                                ... state,
                                books: [],
                                loading: false,
                                error: action.payload
                            };
    }
};

export default reducer;
